import {createReducer, on} from "@ngrx/store";
import {highlightRegion} from "../actions/countries.actions";


export const initialState = '';

const _regionHighlightReducer = createReducer(
  initialState,
  on(highlightRegion, (state, { region }) => region)
);

export function regionHighlightReducer(state, action) {
  return _regionHighlightReducer(state, action);
}
