import {Component, OnInit} from '@angular/core';
import {CountriesService} from "./services/countries.service";
import {CountryMapper} from "./mappers/country.mapper";
import {Country} from "./models/country.model";
import { CdkDrag, CdkDragDrop, moveItemInArray } from '@angular/cdk/drag-drop';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit{
  title = 'countries-app';
  regions = ['Africa', 'Americas', 'Asia', 'Europe', 'Oceania'];

  countries: Array<Country> = [];

  constructor(private countriesService: CountriesService) { }

  ngOnInit() {
    this.countriesService.fetchAllCountries().subscribe(res => {
      this.countries = CountryMapper.fromCountryDtoToCountry(res);
    })
  }

  // onDrop(event: CdkDragDrop<string[]>) {
  //   const country = this.countries[event.previousIndex];
  //   const region = event.container.element.nativeElement.id;
  //   country.region = region;
  //   moveItemInArray(this.countries, event.previousIndex, event.currentIndex);
  // }

  onDrop(event: CdkDragDrop<string>) {
    const droppedCountry = event.item.data;
    const targetRegion = event.container.data; // use event.container.data directly
    const currentIndex = this.countries.indexOf(droppedCountry);
    this.countries.splice(currentIndex, 1);
    const targetRegionIndex = this.regions.indexOf(targetRegion);
    const nextCountryIndex = this.getNextCountryIndex(targetRegionIndex, currentIndex);
    this.countries.splice(nextCountryIndex, 0, droppedCountry);
  }

  getNextCountryIndex(regionIndex: number, currentIndex: number) {
    let nextIndex = this.countries.length;
    for (let i = currentIndex + 1; i < this.countries.length; i++) {
      if (this.regions.indexOf(this.countries[i].region) > regionIndex) {
        nextIndex = i;
        break;
      }
    }
    return nextIndex;
  }

  getRegionCard(country: Country): HTMLElement {
    const region = country.region;
    return document.getElementById(region) as HTMLElement;
  }



}
<div class="page-layout">
  <div class="regions-container">
    <mat-card class="region-card" *ngFor="let region of regions" [id]="region"
              [ngClass]="{'highlight': region == hoveredRegion}"
              (dragover)="onRegionOver($event, region)"
              (dragleave)="onRegionLeave($event)">
      <div class="region-title">{{region}}</div>
      <div cdkDropList [cdkDropListConnectedTo]="[region]" (cdkDropListDropped)="onDrop($event, region)">
      </div>
    </mat-card>
  </div>
  <div class="cards-container">
    <mat-card *ngFor="let country of countries" cdkDrag [cdkDragBoundary]="getRegionCard(country)"
              (dragstart)="onDragStart($event, country)"
              (dragend)="onDragEnd($event)">
      <mat-card-content>
        <div>
          <img style="width: 50px" [src]="country.flag">
        </div>
        <div>
          {{country.name}}
        </div>
      </mat-card-content>
    </mat-card>
  </div>
</div>
