import {Action, createReducer, on} from "@ngrx/store";
import {highlightRegion} from "../actions/countries.actions";

export const initialState = '';

const _highlightedRegionReducer = createReducer(
  initialState,
  on(highlightRegion, (state, { region }) => {
    console.log(`highlightedRegionReducer: Setting region to ${region}`);
    return region;
  })
);


export function highlightedRegionReducer(state: string = '', action: Action) {
  return _highlightedRegionReducer(state, action);
}
